//______________________________________________________________ordenamiento
export function ordenamiento(array,by) {
    const newArray = [];
    if (by[0] === "9") {
    //__________________________________________ordena por abecedario
    for (let i = 0; i < array.length; i++) {
      if (array[i].name[0] === by[1]) {
        newArray.push(array[i]);
      }
    }
    return newArray;
  } else {
    //_________________________________________________________________orden por select
    newArray = array.name
    var aux;

    for (var i = 0; i < array.length - 1; i++) {
      for (var j = i + 1; j < array.length; j++) {
        if (by === "asc") {
          if (array[i].name > array[j].name) {
            aux = array[j];
            array[j] = array[i];
            array[i] = aux;
          }
        }
        if (by === "des") {
          if (array[i].name < array[j].name) {
            aux = array[j];
            array[j] = array[i];
            array[i] = aux;
          }
        }
        if (by === "cont") {
          if (array[i].continent > array[j].continent) {
            aux = array[j];
            array[j] = array[i];
            array[i] = aux;
          }
        }
        if (by === "area") {
          if (array[i].area > array[j].area) {
            aux = array[j];
            array[j] = array[i];
            array[i] = aux;
          }
        }
        if (by === "act") {
          if (array[i].ActTurs.length < array[j].ActTurs.length) {
            aux = array[j];
            array[j] = array[i];
            array[i] = aux;
          }
        }
        if (by === "pob") {
          if (array[i].poblation < array[j].poblation) {
            aux = array[j];
            array[j] = array[i];
            array[i] = aux;
          }
        }
      }
    }

    return newArray;
  }
}
