import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { connect } from "react-redux";
import { useHistory } from "react-router";
require("./addActivity.css");

function AddActivity(props) {
  const history = useHistory();
  const { countries } = props;
  console.log(props);
  var [state, setState] = useState({
    name: "",
    duration: 0,
    season: "",
    countries: ["brazil", "uruguay"],
  });

  //_______________________________________________________
  function validate({ value, name }) {
    if (name === "input") {
      setState({ ...state, name: value.toLowerCase().replace(" ", "") });
      if (value.length > 50) {
        setState({ ...state, name: value.slice(0, 50) });
      }
    }
  }

  return (
    <div>
      <h3>name</h3>
      <input
        value={state.name}
        name="input"
        onChange={(e) => validate(e.target)}
      ></input>
      <h3>duracion</h3>
      <div>
        <button name="mas" onClick={(e) => validate(e.target)}>
          +
        </button>
        <button name="menos" onClick={(e) => validate(e.target)}>
          -
        </button>
      </div>
      <h3>temporada</h3>
      <div>
        <button
          name="invierno"
          onClick={(e) => setState({ ...state, season: e.target.name })}
        >
          invierno
        </button>
        <button
          name="verano"
          onClick={(e) => setState({ ...state, season: e.target.name })}
        >
          verano
        </button>
        <button
          name="otoño"
          onClick={(e) => setState({ ...state, season: e.target.name })}
        >
          otoño
        </button>
        <button
          name="primavera"
          onClick={(e) => setState({ ...state, season: e.target.name })}
        >
          primavera
        </button>
      </div>
      <select>
        ciudad
        {countries.map((coun) => (
          <opcions key={coun.id}>{coun.name}</opcions>
        ))}
      </select>

      <form onSubmit={() => {}}>
        <button type="submit">ENVIAR</button>
      </form>
    </div>
  );
}

function mapStateToProps(state) {
  return state;
}

export default connect(mapStateToProps, {})(AddActivity);
