import { Link } from "react-router-dom";
import { connect, useDispatch } from "react-redux";
import React, { useEffect } from "react";
import Nav from "../f_nav/nav";
import { getCountries } from "../../f_redux/f_actions/actions";
import { useState } from "react";

require("./home.css");

require("./home.css");

function Home({ state }) {
  var { countries } = state;
  const dispatch = useDispatch();
  var [pag,setPag]=useState(0)
  useEffect(() => {
    dispatch(getCountries());
  }, []);


 function changePag(e){
     const name=e.target;
     if(pag>=0 && pag < countries.length+10){
        if(name==="sig"){
                setPag(pag+9)
        }else{
                setPag(pag-9)
        }
     }
 } 

  if (countries) {
    countries=countries.slice(pag,pag+9)
    return (
      <div className="homeDiv">
        <Nav></Nav>
        <div className="divButtonsPage">
          <button name="sig" onClick={(e) => changePag(e)}>{"<<"}</button>
          <button name="ant" onClick={(e) => changePag()}>{">>"}</button>
          <button>A-Z</button>
          <button>Z-A</button>
        </div>
        <div className="divRowColCountries">
          {countries.map((ct) => (
            <div className="div-comp-country" key={ct.id}>
              <Link to={`/home/details/${ct.id}`}>
                <h4>{ct.name}</h4>
              </Link>
              <h4>{ct.continent}</h4>
              <Link to={`/home/details/${ct.id}`}>
                <img className="imgFlat" src={ct.imgflat} alt=""></img>
              </Link>
            </div>
          ))}
        </div>
      </div>
    );
  } else {
    return (
      <div className="homeDiv">
        <Nav></Nav>
        <h4>cargando</h4>
      </div>
    );
  }
}
function mapStateToProps(state) {
  return { state };
}

export default connect(mapStateToProps, {})(Home);
