import { connect, useDispatch } from "react-redux";
import React, { useEffect } from "react";

import {
  clear,
  getCountryId,
  getCountryName,
} from "../../f_redux/f_actions/actions";
import search from "../../f_img/search gif.gif";
require("./details.css");

function Details(props) {
  const dispath = useDispatch();
  const { id, countriesNAME_ID } = props;
  useEffect(() => {
    if (id.length > 3) {
      dispath(getCountryName(id));
    } else {
      dispath(getCountryId(id));
    }
  }, []);

  useEffect(() => {
    return () => dispath(clear());
  }, []);

  if (countriesNAME_ID) {
    return (
      <div className="fondo-details">
        <button>x</button>
        {
          countriesNAME_ID.map(
            (elem) => {
              const {
                id,
                imgflat,
                name,
                continent,
                cap,
                area,
                poblation,
                ActTurs,
              } = elem;

              return (
                <div className="div-details-country" key={elem.id}>
                  <img src={imgflat} />
                  <div>
                    <h3>{id}</h3>
                    <h3>{name}</h3>
                    <h3>{continent}</h3>
                    <h3>{cap}</h3>
                    <h3>{area}</h3>
                    <h3>{poblation}</h3>
                    <h3>{}</h3>
                  </div>
                  <div>
                    {ActTurs.length > 0 ? (
                      <ul>
                        <h3>Actividad turistica</h3>
                        {ActTurs.map((e) => (
                          <div key={e.id} className="div-activity">
                            <li>{e.difficulty}</li>
                            <li>{e.season}</li>
                            <li>{e.duration}</li>
                            <li>{e.name}</li>
                          </div>
                        ))}
                      </ul>
                    ) : (
                      <></>
                    )}
                  </div>
                </div>
              );
            } //map
          ) //map
        }
      </div>
    );
  } else {
    return (
      <div>
        <img src={search} alt="" />
      </div>
    );
  }
}

//_____________________________________________________________
function mapStateToProps(state) {
  return state;
}

export default connect(mapStateToProps, {})(Details);
