import { connect, useDispatch } from "react-redux";
import React, { useEffect } from "react";

import {
  clear,
  getCountryId,
  getCountryName,
} from "../../f_redux/f_actions/actions";
import search from "../../f_img/search gif.gif";
require("./details.css");

function Details(props) {
  const dispath = useDispatch();
  const { id, countriesNAME_ID } = props;
  useEffect(() => {
    if (id.length > 3) {
      dispath(getCountryName(id));
    } else {
      dispath(getCountryId(id));
    }
  }, []);

  useEffect(() => {
    return () => dispath(clear());
  }, []);

  if (countriesNAME_ID) {
    return (
      <div className="fondo-details">
        <button className="button-home-details" onClick={() => {}}></button>
        {
          countriesNAME_ID.map(
            (elem) => {
              const {
                id,
                imgflat,
                name,
                continent,
                cap,
                area,
                poblation,
                ActTurs,
              } = elem;

              return (
                <div className="div-details-country" key={elem.id}>
                  <div className="div-left-details">
                    <img src={imgflat} />
                    <h2>{id}</h2>
                    <h2>{name}</h2>
                    <h2>{continent}</h2>
                    <h2>{cap}</h2>
                    <h2>{area}</h2>
                    <h2>{poblation}</h2>
                  </div>
                  <fieldset className="details-activity-tur">
                    <legend>
                      <h2>Actividad turistica</h2>
                    </legend>
                      <h2>name | temporada | duracion </h2>
                    {ActTurs.length > 0 ? (
                      <div>
                        {ActTurs.map((e) => (
                          <div key={e.id} className="div-activity">
                            <h2>{e.name}</h2>
                            <h2>{e.difficulty}</h2>
                            <h2>{e.season}</h2>
                            <h2>{e.duration}</h2>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <></>
                    )}
                  </fieldset>
                </div>
              );
            } //map
          ) //map
        }
      </div>
    );
  } else {
    return (
      <div>
        <img src={search} alt="" />
      </div>
    );
  }
}

//_____________________________________________________________
function mapStateToProps(state) {
  return state;
}

export default connect(mapStateToProps, {})(Details);
