//______________________________________________________________ordenamiento
export function ordenamiento(array,by) {
  console.log("ke", array);

  const newArray = [];
 
  if (by[0] === "9") {
    //__________________________________________ordena por abecedario
    var newArr = [];
    for (let i = 0; i < array.length; i++) {
      if (array[i].name[0] === by[1]) {
        newArray.push(array[i]);
      }
    }
    return newArr;
  } else {
    //_________________________________________________________________orden por select
    var aux;

    for (var i = 0; i < array.length - 1; i++) {
      for (var j = i + 1; j < array.length; j++) {
        if (by === "asc") {
          if (array[i].name > array[j].name) {
            aux = array[j];
            array[j] = array[i];
            array[i] = aux;
          }
        }
        if (by === "des") {
          if ([i].name < [j].name) {
            aux = [j];
            [j] = [i];
            [i] = aux;
          }
        }
        if (by === "cont") {
          if ([i].continent > [j].continent) {
            aux = [j];
            [j] = [i];
            [i] = aux;
          }
        }
        if (by === "area") {
          if ([i].area > [j].area) {
            aux = [j];
            [j] = [i];
            [i] = aux;
          }
        }
        if (by === "act") {
          if ([i].ActTurs.length < [j].ActTurs.length) {
            aux = [j];
            [j] = [i];
            [i] = aux;
          }
        }
        if (by === "pob") {
          if ([i].poblation < [j].poblation) {
            aux = [j];
            [j] = [i];
            [i] = aux;
          }
        }
      }
    }

    return newArray;
  }
}
