//______________________________________________________________ordenamiento
export function ordenamiento(array,by) {
  console.log("ke", array);

  const newArray = [];
 
  if (by[0] === "9") {
    //__________________________________________ordena por abecedario
    var newArr = [];
    for (let i = 0; i < array.length; i++) {
      if (array[i].name[0] === by[1]) {
        newArray.push(array[i]);
      }
    }
    return newArr;
  } else {
    //_________________________________________________________________orden por select
    var aux;

    for (var i = 0; i < array.length - 1; i++) {
      for (var j = i + 1; j < array.length; j++) {
        if (by === "asc") {
          if (array[i].name > array[j].name) {
            aux = array[j];
            array[j] = array[i];
            array[i] = aux;
          }
        }
        if (by === "des") {
          if (ne[i].name < ne[j].name) {
            aux = ne[j];
            ne[j] = ne[i];
            ne[i] = aux;
          }
        }
        if (by === "cont") {
          if (ne[i].continent > ne[j].continent) {
            aux = ne[j];
            ne[j] = ne[i];
            ne[i] = aux;
          }
        }
        if (by === "area") {
          if (ne[i].area > ne[j].area) {
            aux = ne[j];
            ne[j] = ne[i];
            ne[i] = aux;
          }
        }
        if (by === "act") {
          if (ne[i].ActTurs.length < ne[j].ActTurs.length) {
            aux = ne[j];
            ne[j] = ne[i];
            ne[i] = aux;
          }
        }
        if (by === "pob") {
          if (ne[i].poblation < ne[j].poblation) {
            aux = ne[j];
            ne[j] = ne[i];
            ne[i] = aux;
          }
        }
      }
    }

    return newArray;
  }
}
