import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { connect } from "react-redux";
import { useHistory } from "react-router";
require("./addActivity.css");

function AddActivity(props) {
  const history = useHistory();
  const { countries } = props;
  console.log(props);
  var [state, setState] = useState({
    name: "",
    duration: 0,
    season: "",
    countries: ["brazil", "uruguay"],
  });

  //_______________________________________________________
  function validateName(value) {
    setState({ ...state, name: value.toLowerCase().replace(" ", "") });
    if (value.length > 50) {
      setState({ ...state, name: value.slice(0, 50) });
    }
  }

  return (
    <div>
      <h3>name</h3>
      <input
        value={state.name}
        onChange={(e) => validateName(e.target.value)}
      ></input>
      <h3>duracion</h3>
      <div>
        <button onClick={()=>setState({...state,})}>+</button>
        <button>-</button>
      </div>
      <h3>temporada</h3>
      <div>
        <button
          name="invierno"
          onClick={(e) => setState({ ...state, season: e.target.name })}
        >
          invierno
        </button>
        <button
          name="verano"
          onClick={(e) => setState({ ...state, season: e.target.name })}
        >
          verano
        </button>
        <button
          name="otoño"
          onClick={(e) => setState({ ...state, season: e.target.name })}
        >
          otoño
        </button>
        <button
          name="primavera"
          onClick={(e) => setState({ ...state, season: e.target.name })}
        >
          primavera
        </button>
      </div>
      <h3>ciudad</h3>
      <ul>
        {countries.map((coun) => (
          <li key={coun.id}>{coun.name}</li>
        ))}
      </ul>

      <form onSubmit={() => {}}>
        <button type="submit">ENVIAR</button>
      </form>
    </div>
  );
}

function mapStateToProps(state) {
  return state;
}

export default connect(mapStateToProps, {})(AddActivity);
