import { connect, useDispatch } from "react-redux";
import React, { useEffect, useState } from "react";

require("./addActivity.css");

 function AddActivity(props) {
  const {countries}=props;
  var [state, setState] = useState({
    name: "",
    duration: 0,
    season: [],
    countries: [],
  });

  //_______________________________________________________
  function validateName(value) {
    setState({ ...state, name: value.toLowerCase().replace(" ", "") });
    if (value.length > 50) {
      setState({ ...state, name: value.slice(0, 50) });
    }
  }

  return (
    <div>
      <form onSubmit={() => {}}>
        <h3>name</h3>
        <input
          value={state.name}
          onChange={(e) => validateName(e.target.value)}
        ></input>
        <h3>duracion</h3>
        <div>
          <button>+</button>
          <button>-</button>
        </div>
        <h3>temporada</h3>
        <div>
          <button name="invierno">invierno</button>
          <button name="verano">verano</button>
          <button name="otoño">otoño</button>
          <button name="primavera">primavera</button>
        </div>
        <h3>ciudad</h3>
        {countries.map(co)}
        <div>
            <ul>
                <li></li>
            </ul>
        </div>

        <button type="submit">ENVIAR</button>
      </form>
    </div>
  );
}

function mapStateToProps(state){
    return state;
}

export default connect(mapStateToProps,{})(AddActivity)
